import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Component } from 'react';
import { Button, Container, Navbar, NavbarBrand, Nav, NavItem, NavLink, Collapse, NavbarToggler } from 'reactstrap';

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false, // Estado del menú hamburguesa
      isCategoriesOpen: false, // Estado del menú de categorías
    };
  }

  toggleNavbar = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  toggleCategories = () => {
    this.setState({ isCategoriesOpen: !this.state.isCategoriesOpen });
  };

  render() {
    const categorias = [...new Set(this.props.productos.map(producto => producto.categoria))];

    return (
      <Navbar color="dark" dark expand="md">
        <NavbarBrand href="/">Mi App</NavbarBrand>
        <NavbarToggler onClick={this.toggleNavbar} />
        <Collapse isOpen={this.state.isOpen} navbar>
          <Nav className="ml-auto" navbar>
            <NavItem className="mx-2">
              <NavLink href="#">Inicio</NavLink>
            </NavItem>
            <NavItem className="mx-2">
              <NavLink href="#">Acerca</NavLink>
            </NavItem>

            {/* Botón "Categorías" */}
            <NavItem className="mx-2">
              <div className="categories-button text-light" onClick={this.toggleCategories} style={{ cursor: 'pointer' }}>
                Categorías ▼
              </div>
            </NavItem>

            {/* Menú desplegable de categorías */}
          </Nav>

          <Collapse isOpen={this.state.isCategoriesOpen} navbar>
            <Nav className="ml-auto" navbar>
              {categorias.map((categoria, index) => (
                <NavItem key={index} className="mx-2">
                  <NavLink href={`#${categoria}`}>{categoria}</NavLink>
                </NavItem>
              ))}
            </Nav>
          </Collapse>
        </Collapse>
      </Navbar>
    );
  }
}

const Footer = () => (
  <footer className="bg-dark text-light text-center py-3 mt-5">
    <p>&copy; 2025 Mi Aplicación. Todos los derechos reservados.</p>
  </footer>
);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      productos: [],
    };
  }

  componentDidMount() {
    fetch('https://icarosproject.com/2daw/pieles.json')
      .then(response => response.json())
      .then(data => {
        this.setState({ productos: data.productos });
      })
      .catch(error => console.error('Error fetching data:', error));
  }

  render() {
    return (
      <div className="App">
        <Header productos={this.state.productos} />

        {/* Contenido principal */}
        <Container className="mt-4 text-center">
          <h1>Bienvenido a mi aplicación</h1>
          <Button color="primary">Dale</Button>
        </Container>

        <Footer />
      </div>
    );
  }
}

export default App;
